mapper.select:
select CATE_IDX, CATE_NAME, CATE_NICK, CATE_ICON, CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE, CATE_ORDER ,CASE WHEN CATE_ORDER > 0 THEN '노출' ELSE '미노출' END CATE_ORDER_STR from category where CATE_TYPE='BRAND'
order by CATE_ORDER asc
limit #{start},#{length}

SELECT CATE_IDX, CATE_NAME,
CREATED_BY, CREATED_DATE, MODIFIED_BY, MODIFIED_DATE FROM CATEGORY WHERE CATE_DEPTH=#{cateDepth} AND CATE_TYPE='CATEGORY'
limit #{start},#{length}
