mapper.select:
SELECT *, CASE WHEN COM_TYPE = 'INDIV' THEN '개인' WHEN COM_TYPE = 'BMAN' THEN '개인사업자' WHEN COM_TYPE = 'CORPO' THEN '법인' END AS 'COM_TYPE_CHANGE', CASE WHEN COM_STATE = 'NORMAL' THEN '정상업체' WHEN COM_STATE = 'STOP' THEN '거래중지' WHEN COM_STATE = 'ETC' THEN '기타' END AS 'COM_STATE_CHANGE' FROM COMPANY LEFT JOIN COMPANY_EMPLOYEE ON COM_CE_IDX = CE_IDX WHERE COM_IDX IS NOT NULL
ORDER BY COM_IDX DESC
limit #{start}, #{length}
