mapper.select:
SELECT G_IDX, G_STATE, P_NAME, G_BARCODE, IN_SPEC1.SPEC_LOOKUP IN_SPEC1, SELL_SPEC1.SPEC_LOOKUP SELL_SPEC1 FROM GOODS LEFT JOIN PRODUCT ON G_P_IDX = P_IDX LEFT JOIN PO ON G_PO_IDX = PO_IDX LEFT JOIN PO_PRODUCT ON PO_IDX = PP_PO_IDX AND P_IDX = PP_P_IDX LEFT JOIN CHECK_GOODS ON G_IDX = CGS_G_IDX LEFT JOIN CHECK_GROUP ON CGS_CG_IDX = CG_IDX LEFT JOIN SPEC AS IN_SPEC1 ON PP_SPEC_IDX = IN_SPEC1.SPEC_IDX LEFT JOIN SPEC AS SELL_SPEC1 ON PP_SPEC_IDX2 = SELL_SPEC1.SPEC_IDX
ORDER BY G_IDX DESC
LIMIT #{start},#{length}

mapper.delete:
DELETE FROM
    GOODS_TEMP
WHERE
    GT_GGT_IDX = #{ggtIdx}
AND
    GT_G_IDX = #{gIdx}

mapper.select:
