mapper.select:
SELECT * FROM GOODS LEFT JOIN PRODUCT ON G_P_IDX = P_IDX
ORDER BY G_IDX DESC
LIMIT #{start},#{length}

mapper.delete:
DELETE FROM
    GOODS_TEMP
WHERE
    GT_GGT_IDX = #{ggtIdx}
AND
    GT_G_IDX = #{gIdx}

mapper.select:
SELECT P_IDX, P_OPTION, CATE_SPEC_LIST, G_PO_IDX, P_STATE, P_COST, P_SELL_PRICE, P_MEMO, P_MODEL FROM GOODS LEFT JOIN PRODUCT ON G_P_IDX = P_IDX LEFT JOIN CATEGORY ON P_CATE_IDX = CATE_IDX WHERE
#{item}
AND G_POR_IDX IS NULL GROUP BY G_P_IDX, G_OPTION

SELECT P_IDX, POR_OPTION, CATE_SPEC_LIST, G_PO_IDX, P_STATE, P_COST, P_SELL_PRICE, P_MEMO, P_MODEL, SPEC_LOOKUP, POR_IDX FROM GOODS LEFT JOIN PO_RECEIVED ON G_POR_IDX = POR_IDX LEFT JOIN PRODUCT ON G_P_IDX = P_IDX LEFT JOIN CATEGORY ON P_CATE_IDX = CATE_IDX LEFT JOIN SPEC ON POR_PROCESS_SPEC_IDX = SPEC_IDX WHERE
#{item}
GROUP BY POR_IDX
