mapper.select:
SELECT GS_IDX, GS_THUMB_FILE1, P_STATE, P_NAME, P_IDX, P_MODEL, P_COST, P_SELL_PRICE, GS_CODE, GS_NAME, GS_PRICE, GS_SALE, GS_RANK, GS_SAFE_STOCK, GS_PROPER_STOCK, GS_DAILY, goods_stock.CREATED_BY, goods_stock.CREATED_DATE, goods_stock.MODIFIED_BY, goods_stock.MODIFIED_DATE, ifnull(GS_OPTION ->>'$.OPTION1','') OPTION1, ifnull(GS_OPTION ->>'$.OPTION2','') OPTION2, ifnull(GS_OPTION ->>'$.OPTION3','') OPTION3 FROM goods_stock LEFT JOIN product ON GS_P_IDX=P_IDX WHERE GS_IDX IS NOT null
order by P_IDX desc
limit #{start},#{length}
